package coding;

public class Pyramid {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int k=1,i,j;
		
		for(i =1;i<5;i++)
		{
			
			for(j=1;j<=i;j++)
				
			{
				System.out.print(k);
				System.out.print("\t");
				k++;
				
			}
			System.out.println();
		}
		

	}

}
